import {themeDark,themeLight,htmlDark,htmlLight,txtLight,txtDark} from './Styles';

const styles=themeLight;
const htmlStyles=htmlLight;
const txtStyles=txtLight;
    
export default class DetailScreen extends React.Component{

    static navigationOptions=({navigation})=>({
        title:navigation.state.params.data.name,
        headerStyle:{marginTop: Platform.OS ==='ios' ? 0 : -30 },
        style:{height:30},
    });
    constructor(props) {
        super(props);
        this.state = {
          visible: false
        }
        AsyncStorage.getItem('theme',(err,result)=>{
            console.log('stored '+result);
            if(result=='Light'){
                styles=themeLight;
                htmlstyles=htmlLight;
                //txtStyles=txtLight;
                console.log('using themeLight htmlLight ');
            } 
            if(result=='Dark'){
                styles=themeDark;
                htmlstyles=htmlDark;
                //txtStyles=txtDark;
                console.log('using themeDark htmlDark ');
            } 
        });
      }
      onCancel() {
        console.log("CANCEL")
        this.setState({visible:false});
      }
      onOpen() {
        console.log("OPEN")
        this.setState({visible:true});
      }

    async MarkRead(){
        const {params} = this.props.navigation.state;
        const at = await AsyncStorage.getItem('@FB:at');
        
        console.log('marking read ');
        const id=params.data.id;
        console.log('id:'+id+' at:'+at);
        url='http://oliang.itban.com/readpost/'+id;
        console.log('url:'+url+' at:'+at);
        return fetch(url,{
            method:'get',
            headers:{
                'Authorization':'Basic '+at
            }
        })
        
        
    }
    render(){
        AsyncStorage.getItem('theme',(err,result)=>{
            console.log('stored '+result);
            if(result=='Light'){
                styles=themeLight;
                htmlstyles=htmlLight;
                //txtStyles=txtLight;
                console.log('using themeLight htmlLight ');
            } 
            if(result=='Dark'){
                styles=themeDark;
                htmlstyles=htmlDark;
                //txtStyles=txtDark;
                console.log('using themeDark htmlDark ');
            } 
        });
        this.MarkRead();
        const {params} = this.props.navigation.state;
        let contentURL="http://oliang.itban.com/content/"+params.data.id;
        let shareOptions = {
            title: params.data.title,
            message: params.data.content,
            url: contentURL,
            subject: "Share from Oliang NBTC" //  for email
          };
      
          let shareImageBase64 = {
            title: params.data.title,
            message: "Hola mundo",
            url: params.data.img,
            subject: "Share from Oliang NBTC" //  for email 
          };
          let shareContent={
              message: params.data.title+': '+params.data.content,
              title: params.data.title,
              url:contentURL,
          }
        if(params.data.mp4!==""){
            let clip_url=params.data.mp4.replace('.com/media/','.com:8081/vod/oliang/')+'/playlist.m3u8';
            console.log('vdo content');
            
            return (
                <View style={{flex:1,backgroundColor:'#000',alignContent:'center',alignItems:'center',justifyContent:'center'}} >
                
                <Expo.Video source={{uri:clip_url}} style={styles.img}
                    resizeMode="contain" 
                    shouldPlay={true}
                    hls={true}                    
                    />
                </View>
            )
        }else{

//<HTMLView style={styles.content} value={params.data.content} />
//<WebView hasZoom='true' source={{uri:'http://oliang.itban.com/content/'+params.data.id}} style={{marginTop:20}} />
          return(
              <ScrollView style={styles.container}>
                  <View style={{flex:1,height:300,position:'relative'}}>
                  <Image resizeMode="cover" source={{uri:params.data.img}} style={styles.img}/>
                  </View>
              <View style={{padding:10}} >
              <Text style={styles.title}>{params.data.title}</Text>
              <HTMLView hasZoom='true' stylesheet={htmlStyles}  value={params.data.content}   />
              </View>         
      <Button onPress={()=>{Share.share(shareContent)}} title="Share" style={styles.btn} ></Button>
              </ScrollView>
          )
        }
    }
}
